wManager.Events.InteractEvents.OnInteractPulse += (target, cancelable) =>
{
    var t = wManager.Wow.ObjectManager.ObjectManager.GetObjectByGuid(target);
    if (!t.IsValid)
        return;
    var e = t.Entry;
    if (e == 144064 || e == 144050 || e == 177681)
    {
        System.Threading.Tasks.Task.Run(delegate {
            System.Threading.Thread.Sleep(wManager.Wow.Helpers.Usefuls.Latency + 100);
            wManager.Wow.Helpers.ClickOnTerrain.Item(9466, t.Position);
            System.Threading.Thread.Sleep(wManager.Wow.Helpers.Usefuls.Latency + 600);
wManager.wManagerSetting.CurrentSetting.ListHarvest.Add(177681);
wManager.wManagerSetting.CurrentSetting.ListHarvest.Add(144064);

        });
    }
};

