            if (p == null)
            {
                p = new Quester.Bot.ProfileUserSettings { ProfilePath = Quester.Bot.QuesterSetting.CurrentSetting.ProfileName };
                Quester.Bot.QuesterSetting.CurrentSetting.ProfilesUserSettings.Add(p);
            }

            if (ObjectManager.Me.WowClass == WoWClass.Druid)
            {
                if (ObjectManager.Me.Level >= 1 && ObjectManager.Me.Level <= 60)
                {
                    if (!p.SkipSteps.Contains(16) && !p.SkipSteps.Contains(17) && !p.SkipSteps.Contains(18) && !p.SkipSteps.Contains(19) && !p.SkipSteps.Contains(20) && !p.SkipSteps.Contains(21)
                        && !p.SkipSteps.Contains(22) && !p.SkipSteps.Contains(23) && !p.SkipSteps.Contains(28) && !p.SkipSteps.Contains(29) && !p.SkipSteps.Contains(30) && !p.SkipSteps.Contains(31) && !p.SkipSteps.Contains(32) && !p.SkipSteps.Contains(33)
                        && !p.SkipSteps.Contains(34) && !p.SkipSteps.Contains(35) && !p.SkipSteps.Contains(36) && !p.SkipSteps.Contains(37) && !p.SkipSteps.Contains(38) && !p.SkipSteps.Contains(39)
                        && !p.SkipSteps.Contains(40) && !p.SkipSteps.Contains(41) && !p.SkipSteps.Contains(42) && !p.SkipSteps.Contains(43) && !p.SkipSteps.Contains(44) && !p.SkipSteps.Contains(45)
                        && !p.SkipSteps.Contains(46) && !p.SkipSteps.Contains(47) && !p.SkipSteps.Contains(48) && !p.SkipSteps.Contains(49) && !p.SkipSteps.Contains(50) && !p.SkipSteps.Contains(51)
                        && !p.SkipSteps.Contains(52) && !p.SkipSteps.Contains(53) && !p.SkipSteps.Contains(54) && !p.SkipSteps.Contains(55) && !p.SkipSteps.Contains(56) && !p.SkipSteps.Contains(57)
