var position = new Vector3(-3828.88f, -4517.51f, 10.66067f);
int npcEntryId = 4321;

if (!ObjectManager.Me.IsOnTaxi)
{
    if (wManager.Wow.Bot.Tasks.GoToTask.ToPositionAndIntecractWithNpc(position, npcEntryId))
    {
        Usefuls.SelectGossipOption(GossipOptionsType.taxi);

string returnVariableName = "";

returnVariableName = wManager.Wow.Helpers.Lua.LuaDoString("for i=0,30 do if string.find(TaxiNodeName(i),'Desolace') then return i end end", returnVariableName);

int node = Convert.ToInt32(returnVariableName);

        wManager.Wow.Helpers.Lua.LuaDoString("TakeTaxiNode(" + node + ")");

    }
}

